        <div><span className='st'>Enter new voting time : </span>
                <input className='input_new' type="text" id="vote_time"/>
            </div>
        <button onClick={() => this.submit()}>Submit</button>
        <button onClick={this.props.closePopup}>Done</button>
        </div>
      </div>
    );
  }
}





